import React from 'react';
import pythonCert from '../assets/certificates/python.jpg';
import commCert from '../assets/certificates/communication.jpg';
import vizCert from '../assets/certificates/visualization.jpg';

const certificates = [
  {
    title: 'Python for Data Science – NPTEL',
    image: pythonCert,
    description: 'Completed a full-term course covering data science concepts and Python tools like Pandas and NumPy.'
  },
  {
    title: 'Business & Communication Skills – NPTEL',
    image: commCert,
    description: 'Improved communication, presentation, and team collaboration techniques in a business setting.'
  },
  {
    title: 'Data Visualization Program – Forage',
    image: vizCert,
    description: 'Built dashboards and visualizations using Excel and Tableau based on real-world datasets.'
  }
];

export default function Certifications() {
  return (
    <section id="certifications" data-aos="fade-up">
      <h2>Certifications</h2>
      <div className="cert-grid">
        {certificates.map((cert, index) => (
          <div key={index} className="cert-card">
            <img src={cert.image} alt={cert.title} />
            <h3>{cert.title}</h3>
            <p>{cert.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
